d(line.split(\",\")[0])        \r\n    fo.close()\r\ncounts = {}\r\nfor name in ls:\r\n    ___(1)___\r\nitems = __(2)___\r\nprint(\"全勤同学有：\",end =\"\")\r\nfor i in range(1,75,1):\r\n    ....（3）....        \r\n        print(word,end =\",\")</code></pre>\r\n\r\n<p>&nbsp;</p>', '<pre>\r\n<code class=\"language-python\"># 从csv文件中读取考勤数据\r\nls = []\r\nfor i in range(1,11):\r\n    fo =  open(str(i) +\".csv\",\"r\",encoding = \"utf-8\")\r\n    for line in fo:\r\n        line = line.replace(\"\\n\",\"\")\r\n        ls.append(line.split(\",\")[0])        \r\n    fo.close()\r\ncounts = {}\r\nfor name in ls:\r\n    counts[name] = counts.get(name,0) + 1\r\nitems = list(counts.items())\r\nprint(\"全勤同学有：\",end =\"\")\r\nfor i in range(1,75,1):\r\n    word,count = items[i]\r\n    if count == 10 :\r\n        #print(\"{0:&lt;10}:{1:&lt;5}次\".format(word,count))\r\n        print(word,end =\",\")</code></pre>\r\n\r\n<p>&nbsp;</p>', '8', '0', '2019-03-29 06:39:26.801289', null, '2019-03-29 06:39:26.801289', null, null, '13', '4');
